DIRS = {
  "U" => [-1, 0], # N
  "R" => [0, 1], # E
  "D" => [1, 0], # S
  "L" => [0, -1], # W
}

BOARD = [
"..1..".chars,
".234.".chars,
"56789".chars,
".ABC.".chars,
"..D..".chars,
]

class Array
  def move(vec)
    [ self[0] + vec[0],
      self[1] + vec[1] ]
  end

  def clip
    map { |e| [[0, e].max, 4].min }
  end
end

def board(newpos)
  BOARD[newpos[0]][newpos[1]]
end

pos = [2,0]
input.each do |line|
  p line
  line.each_char do |c|
    # p [pos, board(pos)]
    vec = DIRS[c]
    newpos = pos.move(vec).clip
    pos = newpos unless board(newpos) == "."
  end
  p board(pos)
end


